import React, { useState } from "react";
import IconButton from "@mui/material/IconButton";
import PersonAddOutlinedIcon from "@mui/icons-material/PersonAddOutlined";
import PersonRemoveOutlinedIcon from "@mui/icons-material/PersonRemoveOutlined";
import MoreVertOutlinedIcon from "@mui/icons-material/MoreVertOutlined";
import DeleteIcon from '@mui/icons-material/Delete';
import ErrorIcon from '@mui/icons-material/Error';
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import { Box, Typography } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { setFriends , deletePost } from "../state/index.js";
import FlexBetween from "./FlexBetween";
import UserImage from "./UserImage";


const Friend = ({ friendId, name, subtitle, userPicturePath, postId }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { _id } = useSelector((state) => state.user);
  const token = useSelector((state) => state.token);
  const friends = useSelector((state) => state.user.friends);
  const isFriend = friends.find((friend) => friend._id === friendId);

  const [anchorEl, setAnchorEl] = useState(null);

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const patchFriend = async () => {
    const response = await fetch(
      `http://localhost:4000/users/${_id}/${friendId}`,
      {
        method: "PATCH",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      }
    );
    const data = await response.json();
    dispatch(setFriends({ friends: data }));
  };

  const handleDelete = async () => {
    const response = await fetch(
      `http://localhost:4000/posts/${postId}`,
      {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      }
    );
    const data = await response.json();
    dispatch(deletePost({ post: { _id: postIdToDelete } }));
  };

  return (
    <FlexBetween>
      <FlexBetween gap="1rem">
        <UserImage image={userPicturePath} size="55px" />
        <Box
          onClick={() => {
            navigate(`/profile/${friendId}`);
            navigate(0);
          }}
        >
          <Typography
            variant="h5"
            fontWeight="500"
            sx={{
              "&:hover": {
                cursor: "pointer",
              },
            }}
          >
            {name}
          </Typography>
          <Typography fontSize="0.75rem">{subtitle}</Typography>
        </Box>
      </FlexBetween>
      <Box display="flex" alignItems="center" gap={0}>
        <IconButton onClick={() => patchFriend()}>
          {isFriend ? (
            <PersonRemoveOutlinedIcon />
          ) : (
            <PersonAddOutlinedIcon />
          )}
        </IconButton>
        <IconButton onClick={handleMenuClick}>
          <MoreVertOutlinedIcon />
        </IconButton>
        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleMenuClose}
        >
          <MenuItem onClick={handleDelete}><DeleteIcon ></DeleteIcon>Delete</MenuItem>
          <MenuItem><ErrorIcon></ErrorIcon>Report an Error</MenuItem>
        </Menu>
      </Box>
    </FlexBetween>
  );
};

export default Friend;
