import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import Navbar from '../components/navbar';
import Footer from '../components/footer';
import CustomAlert from './CustomAlert'; // Import your custom alert component

const currentYear = new Date().getFullYear();

export default function Login() {
  const history = useHistory();
  const [credentials, setCredentials] = useState({
    email: '',
    password: '',
  });
  const [showAlert, setShowAlert] = useState(false); // State to control the custom alert
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:4000/api/loginUser', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: credentials.email, password: credentials.password }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log(data); // Handle success response
        localStorage.setItem('userEmail', credentials.email);
        localStorage.setItem('authToken', data.authToken);

        // Set showAlert to true to show the custom alert
        setShowAlert(true);

        // Redirect to the home page
        history.push('/');
      } else {
        throw new Error('Failed to login user');
      }
    } catch (error) {
      console.error(error); // Handle error
    }
  };

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const closeAlert = () => {
    // Set showAlert to false to hide the custom alert
    setShowAlert(false);
  };

  return (
    <>
      <Navbar />
      <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
        {/* ... your existing login form JSX ... */}
        <div className="w-full mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
          <form className="space-y-6" onSubmit={handleSubmit}>
            {/* ... your existing form fields ... */}
            <button
              type="submit"
              className="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
            >
              Log in
            </button>
          </form>
          <p className="text-center mt-3 text-body-secondary">
            Â© {currentYear}, All rights reserved
          </p>
        </div>
        {showAlert && (
          <CustomAlert message="Login Successful" onClose={closeAlert} />
        )}
      </div>
      <Footer />
    </>
  );
}
