import * as React from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import moment from 'moment';

export default function BlogCard(props) {
  return (
    <div className="row">
      <div className="col-6 col-lg-3 col-md-4 col-sm-4 container" style={{ display: 'flex', justifyContent: 'center' }}>
        <Card
            sx={{
              width: {
                xs: "100%", // 100% width for extra small screens
                lg: "75%",  // 75% width for large screens and above
              },
              hie: {
                xs: "100%", // 100% width for extra small screens
                lg: "75%",  // 75% width for large screens and above
              },
              my: 2,
              boxShadow: "5px 5px 10px #ccc",
              ":hover": { boxShadow: "10px 10px 20px #ccc" },
            }}
        >
          <CardMedia
            component="img" 
            height="auto"
            image={props.image}
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              {props.title}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {props.content}
            </Typography>
          </CardContent>
          <hr />
          <CardActions>
            <Typography variant="body2" color="text.secondary">
              <span>
                By:{" "}
                <span className="font-bold text-indigo-700">
                  {props.author}
                </span>
              </span>
            </Typography>
            <span className="ml-1.5">  {moment(props.date).format('lll')}</span>
          </CardActions>
        </Card>
      </div>
    </div>
  );
}
